#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include "solution.h"

using namespace std;

int main(int argc, char *argv[])
{
    double result = 0;
    vector<int> arr;
    double c;

    ifstream fin(argv[1]);
    ifstream fout(argv[2]);
    ofstream fr(argv[3]);

    while(!fin.eof()){
        double tmp;
        fin >> tmp;
        arr.push_back(tmp);
    }
    fout >> c;

    result = solution(arr);

    fr << '[';
    for (int i=0; i< arr.size(); i++){
        fr << arr[i];
        if(i != arr.size() -1 ){
            fr << ", ";
        }
    }
    fr << ']' << endl;
    fr << c << endl;

    if(result == c){
        fr << "테스트를 통과하였습니다." << endl;
    }else{
        fr << "실행한 결괏값 "<< result << "이(가) 기댓값 " << c << "와(과) 다릅니다." << endl;
    }

    fin.close();
    fout.close();
    fr.close();

    return 0;
}
